import Sprite from "./Sprite"
import AnimManager from "./AnimManager"

class TileSprite extends Sprite {
  constructor ( texture, w, h ) {
    super( texture )

    this.tileW = w

    this.tileH = h

    this.frame = {
      x: 0,
      y: 0
    }

    this.anims = new AnimManager( this )
  }

  update ( dt ) {
    this.anims.update( dt )
  }
}

export default TileSprite
